// Get the message from the webhook payload
const message = $input.first().json.body.message || 'Hello';

// Simple chatbot responses
let response = '';

if (message.toLowerCase().includes('hello') || message.toLowerCase().includes('hi')) {
  response = 'Hello! How can I help you today?';
} else if (message.toLowerCase().includes('who') && message.toLowerCase().includes('you')) {
  response = 'I\'m Raphael\'s AI assistant! I can help you learn about his projects, skills, and experience.';
} else if (message.toLowerCase().includes('project')) {
  response = 'Raphael works on various AI and creative technology projects. You can explore the Projects section to see more details!';
} else if (message.toLowerCase().includes('skill')) {
  response = 'Raphael has expertise in machine learning, natural language processing, and interactive design. Check out the Skills section for more!';
} else if (message.toLowerCase().includes('contact')) {
  response = 'You can reach Raphael through the Contact section on this portfolio!';
} else {
  response = `Thanks for your message: "${message}". I\'m here to help you learn more about Raphael\'s work. Try asking about his projects, skills, or experience!`;
}

return {
  output: response,
  message: response
};
